  <body>
    <div class="top-bar">
        <header id="bar">
            <div class="logo">
                <img src="/assets/icons/LOGO_JOHW.svg" alt="">

            </div>
            <div class="menu">
                <img id="menu-bento" src="/assets/icons/MENU_BENTO.svg" alt="" onclick="">
                    <section id="menu-bento-aberto">
                        <img src="/src/assets/icons/">
                        <div class="lista-menu">
                            <div class="container text-center">
                                <div class="row row-cols-1 row-cols-sm-2 row-cols-md-4">
                                    <ul class="lista-item">
                                        <!-- <li *ngFor="let item of listMenu">
                                            <a type="button" class="btn btn-primary" onclick="">
                                                <ng-container *ngIf="item.iconSvg">
                                                    <img [src]="item.iconSvg" alt="">
                                                </ng-container>
                                            </a>
                                            <span class="col">{{item.legenda.toUpperCase()}}</span>
                                        </li> -->
                                    </ul>
                            </div>
                        </div>
                        </div>
                    </section>
            </div>
            <div class="user">

            </div>

            <section class="body-categoria">
                <div class="view-categoria">
                    <div class="background-categoria">
                        <form>
                            <legend>
                            <h1>Cadastrar Categoria</h1>
                            </legend>
                            <div class="container text-center" >
                                <div class="row row-cols-2 row-cols-sm-2 row-cols-md-2"> 

                                  <div class="col">
                                    <label for="descricao">Descrição:</label>
                                    <input type="text" id="descricao" name="descricao" [(ngModel)]="categoria.descricao"
                                    required #descricao="ngModel" autofocus minlength="3" maxlength="50"
                                    [ngClass]="{ 'highlight-error': descricao.invalid && descricao.touched }">
                                    <div class="warning" *ngIf="descricao.invalid && descricao.touched">Campo obrigatório</div>
                                  </div>

                                  <div class="col">
                                    <label for="tipoCategoria">Tipo Categoria:</label>
                                    <input type="text" id="tipoCategoria"  name="tipoCategoria" [(ngModel)]="categoria.tipoCategoria"
                                    required #tipoCategoria="ngModel" minlength="3" maxlength="50"
                                    [ngClass]="{ 'highlight-error': tipoCategoria.invalid && tipoCategoria.touched }">
                                    <div class="warning" *ngIf="tipoCategoria.invalid && tipoCategoria.touched">Campo obrigatório</div>
                                  </div>

                                </div>
                              </div>
                              <div class="button">
                                <input type="submit" class="btn btn-primary" value="Cadastrar">
                                <div *ngIf="exibirMensagem" class="success-message" >
                                    {{sucessMessage}}
                                </div>
                                <input type="button" class="btn btn-primary red" value="Cancelar" (click)="voltarPagina()">
                              </div>

                        </form>
                    </div>
                </div>
            </section>

        </header>
    </div>


    </body>
</html>
