  sortPacientesByNome() {
    this.pacientes = this.pacientes.filter(paciente => paciente.nomePaciente !== undefined);
    this.pacientes.sort((a, b) => {
      const nomeA = a.nomePaciente!.toLowerCase();
      const nomeB = b.nomePaciente!.toLowerCase();
      if (nomeA < nomeB) {
        return -1;
      }
      if (nomeA > nomeB) {
        return 1;
      }
      return 0;
    });
  }
