import { Component } from '@angular/core';
import { ɵDomRendererFactory2 } from '@angular/platform-browser';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Categoria } from 'src/app/models/categoria/categoria';
import { Conta } from 'src/app/models/conta/conta';
import { Nutricionista } from 'src/app/models/nutricionista/nutricionista';

import { Paciente } from 'src/app/models/paciente';
import { ControleCaixaService } from 'src/app/services/controle-caixa.service';
import { ServicePaciente } from 'src/app/services/service-paciente.service';

@Component({
  selector: 'app-home-controle-caixa',
  templateUrl: './home-nutricionista.component.html',
  styleUrls: ['./home-nutricionista.component.scss']
})
export class HomeNutricionistaComponent {

  isClick: Boolean = false;

  exibirPopupExclusao = false;

  sucessMessage: string = "";

  exibirMensagem: boolean = false;

  selectedControle: any;

  controleSelecionado: Nutricionista | null = null;

  pacientes: Paciente[] = [];

  contas: Conta[] = [];

  categorias: Categoria[] = [];

  controles: Nutricionista[] = [];

  controle: Nutricionista = {

    idControleCaixa: 0,
    paciente: new Paciente,
    conta: [],
    data: new Date(),
    produtoOuCliente: '',
    preco: 0,
    formaPagamento: '',
    categoria: new Categoria,
    descricaoControle: '',

  }

  ngOnInit(): void {

    this.listarControles();
    this.listarPacientes();
  }

  constructor(private router: Router, private route: ActivatedRoute,
    private renderer: ɵDomRendererFactory2, private toastr: ToastrService,
    private serviceControle: ControleCaixaService, private pacienteService: ServicePaciente) {

     }

     pages = [
      { nome: "Home", rota: "" },
      { nome: "Agenda", rota: "/agendamentos" },
      { nome: "Consulta", rota: "/consultas" },
      { nome: "Nutricionista", rota: ""},
      { nome: "Categoria", rota: "/categorias"},
      { nome: "Paciente", rota: "/pacientes"},
      // Adicione outras páginas conforme necessário
    ];


    direcionarPagina(pagina: string) {
      // Encontre a página correspondente no array de páginas
      const paginaEncontrada = this.pages.find(p => p.nome.toLowerCase() === pagina.toLowerCase());

      if (paginaEncontrada) {
        console.log("Entrou e clicou");
        // Redirecione para a rota correspondente
        this.router.navigate([paginaEncontrada.rota]);
      }
    }

    clicarMenuBento() {
      this.isClick = !this.isClick;
    }

  listarControles(): void {
    this.serviceControle.listarNutricionista().subscribe(data => {
    this.controles = data;
  })
  }

  listarPacientes() {
    this.pacienteService.getPaciente().subscribe(pacientes => {
      this.pacientes = pacientes;
    });
  }


     excluirControle() {


      if (this.controleSelecionado && this.controleSelecionado.idControleCaixa) {

        const idControle = this.controleSelecionado.idControleCaixa;
        this.serviceControle.deletarControleCaixa(idControle). subscribe( () => {

          this.sucessMessage = "Consulta Excluído!";
          this.exibirMensagem = true;
          setTimeout(() => {
            this.toastr.success(this.sucessMessage, 'Sucesso');
            this.router.navigate(['consultas']);
          }, 2000)
          this.listarControles();

          this.controleSelecionado = null;
          this.exibirPopupExclusao = false;
        });

      }
      else  {
          console.log("Não encontrado ou erro");
      }
    }

    selecionarControle(nutricionista: Nutricionista): void {
      this.controleSelecionado = nutricionista;
    }

    openPopup(controle: any) {
      console.log("Clicou");
      console.log(this.selectedControle = controle);
      this.selectedControle = controle;

    }

    closePopup() {
      this.selectedControle = null;
      this.router.navigate(['/consultas']);
    }

    voltarPagina(): void {
      this.router.navigate(['consultas'])
    }

}
