import { Component, ElementRef, HostListener, OnInit, ViewChild } from '@angular/core';
import { FormControl, NgForm, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Agendamento } from 'src/app/models/agendamento/agendamento';
import { Consulta } from 'src/app/models/consulta/consulta';
import { Paciente } from 'src/app/models/paciente';
import { ServiceConsulta } from 'src/app/services/service-consulta.service';
import { ServicePaciente } from 'src/app/services/service-paciente.service';

@Component({
  selector: 'app-cadastrar-agendamento',
  templateUrl: './cadastrar-agendamento.component.html',
  styleUrls: ['./cadastrar-agendamento.component.scss']
})
export class CadastrarAgendamentoComponent implements OnInit {

  @ViewChild('agendaForm') agendaForm!: NgForm;

  sucessMessage: string = "";

  exibirMensagem: boolean = false;

  paciente: Paciente = {
    nomePaciente: '',
    sobrenomePaciente: '',
    dataNascimentoPaciente: new Date(),
    idadePaciente: 0,
    cidade: '',
    estado: '',
    statusPagamento: '',
    telefone: '',
  };

  consulta: Consulta = {

    idConsulta: 0,

    paciente: new Paciente,

    agendamento: new Agendamento,

    dataInicioConsulta: new Date,

    dataConsultaAtual: new Date,

    dataConsultaAlterada: new Date,

    dataConsultaAntiga: new Date,

    dataUltimaConsulta: new Date,

    tipoConsulta: " ",

    formaPagamento: " ",

    mesesAcompanhado: 0,
  };

  agenda: Agendamento = {

    idConsulta: 0,

    idPaciente: 0,

    dataInicio: new Date,

    descricao: '',

    quantidadeConsulta: 0,

    horarioInicio: new Date,

    horaFinal: new Date,

    lembrete: 0, // Notificação do evento

    observacao: ''
  };

  ngOnInit() : void {
  }

  constructor(private servicePaciente: ServicePaciente, private serviceConsulta: ServiceConsulta, private rota: Router, private el: ElementRef, private toastr: ToastrService) {
  }
}
